      {
        글제목.map(function(a, i){
          return (
            <div className='list' key = {i}>

              <h4 onClick={() => {
                setModal(!modal)
              }}>{ a } <span onClick={() => {
                let copy3 = [...따봉]
                copy3[i] += 1
                따봉변경(copy3)
                }
                }>👍</span> {따봉[i]}  </h4>

              <p>{날짜[i]}</p>
            </div>
          )
        })
      }

      {
        modal == true ? <Modal  글제목 = {글제목} /> : null
      }

    </div>
  );
}

// 컴포넌트 만들기
/* 
  컴포넌트를 언제 많이 사용하는가?
    1. 반복적인 html 축약할 때
    2. 큰 페이지들 만들때
    3. 자주변경 되는 것들
*/
function Modal(props){
  return (
    <div className='modal' style={{backgroundColor : props.color}}>
      <h4>{props.글제목[0]}</h4>
      <p>날짜</p>
      <p>상세내용</p>
    </div>
  )
}

export default App;
